INSERT INTO fzu.`table` (Title, Author, Date, Click, Text) VALUES ('关于福州大学“人事人才工作论坛”改期的通知', '【人事处】', '2021-07-24', 6234, '各学院，机关各部处、各单位：因受台风影响，原定于2021年7月26日（周一）上午9：00在国家大学科技园召开的人事人才工作论坛延期举办，具体时间另行通知。                                             人 事 处                                        2021年7月23日');
