INSERT INTO fzu.`table` (Title, Author, Date, Click, Text) VALUES ('关于公布2019年度校部机关、直属单位考评结果的通知', '【人事处】', '2020-01-08', 3734, '各学院、机关各部处、各单位：根据《福州大学校部机关、直属单位年度绩效考核方案》（福大人〔2018〕11号），经考核，并经2020年第1次校长办公会议审议通过，现将2019年度校部机关、直属单位考评结果公布如下：                                      校部机关、直属单位年度绩效考核工作小组                                                2020年1月8日');
