INSERT INTO fzu.`table` (Title, Author, Date, Click, Text) VALUES ('关于智慧校园平台安全维护的通知', '【网信办】', '2020-07-28', 5982, ' 各学院，机关各部门、各单位：兹定于2020年7月28日22:30至7月29日1:30对智慧校园平台进行安全维护。维护期间统一身份认证、网上办事服务大厅、电子通行码等服务将无法正常访问。不便之处，敬请谅解。                                       网络安全与信息化办公室                               2020年7月28日');
