INSERT INTO fzu.`table` (Title, Author, Date, Click, Text) VALUES ('关于转发《福建省应对新型冠状病毒感染肺炎疫情工作领导小组综合协调组转发关于印...', '【后勤管理处】', '2020-02-15', 4135, '各学院，机关各部处、各单位：为进一步做好新型肺炎疫情防控工作，现将《福建省应对新型冠状病毒感染肺炎疫情工作领导小组综合协调组转发关于印发新冠肺炎流行期间办公场所和公共场所空调通风系统运行管理指南的通知》转发给你们，请遵照执行。特此通知。                                                 福州大学后勤管理处     2020年2月15日 福建省应对新型冠状病毒感染肺炎疫情工作领导小组综合协调组转发关于印发新冠肺炎流行期间办公场所和公共场所空调通风系统运行管理指南的通知.doc ');
