INSERT INTO fzu.`table` (Title, Author, Date, Click, Text) VALUES ('关于智慧校园平台安全维护的通知', '【网信办】', '2021-03-27', 2823, '各学院，机关各部门、各单位：网络安全与信息化办公室拟于2021年3月27日晚10：00至2020年3月28日凌晨2：00对智慧校园平台进行安全维护。维护期间统一身份认证、网上办事服务大厅、教职工健康上报、电子通行码、公寓管理等服务将无法正常访问。不便之处敬请谅解。                          网络安全与信息化办公室2021年3月27日 ');
