INSERT INTO fzu.`table` (Title, Author, Date, Click, Text) VALUES ('关于工程审计截止日期的通知', '【审计处】', '2020-06-28', 2668, ' 各学院，机关各部门、各单位：  为便于统筹安排本学期审计工作，请相关单位将招标控制价或工程结算资料于2020年6月30日前送审。咨询电话：22865587                                         审计处                                     2020年6月28日  ');
