INSERT INTO fzu.`table` (Title, Author, Date, Click, Text) VALUES ('关于举行福州大学第九届“振奋杯”跳绳比赛的通知', '【体育教学研究部】', '2021-04-07', 2191, '各学院：兹定于2021年4月24日（ 周六）8时至4月24日12时在风雨操场（综合体育馆）健美操馆举行福州大学第九届“振奋杯”跳绳比赛（详见附件），请认真做好组织工作和赛前准备。 附件：福州大学第九届“振奋杯”跳绳比赛策划书 福州大学体育运动委员会福州大学体育教学研究部福州大学学生工作部2021年4月7日');
