INSERT INTO fzu.`table` (Title, Author, Date, Click, Text) VALUES ('关于公布福州大学2021年课程思政教材建设项目的通知', '【教务处】', '2021-04-14', 2342, '附件福州大学2021年课程思政教材建设项目名单
