INSERT INTO fzu.`table` (Title, Author, Date, Click, Text) VALUES ('关于学府南路福州大学路段交通管制的友情提示', '【保卫部】', '2020-05-13', 2776, '福州市公安局交通警察支队发布了关于上街（高新区）学府南路部分道路交通管制通告，通告决定从2020年5月13日至7月31日，对上街（高新区）学府南路（南起学府南路建平路辅路口，北至学府南路溪源宫路口）实施交通管制，禁止一切车辆和行人通行。该路段管制后，请教职工从旗山校区东门入校。特此提醒！                          福州大学校长办公室                            福州大学保卫部                             2020年5月13日');
